TS.raw_templates['channel_member_invite_list'] = "{{#if show_ra_tip}}\n\t<p class=\"no_bottom_margin\">\n\t\tType or select the names of team members that you would like to invite:\n\t<\/p>\n\t<p class=\"small_bottom_margin mini subtle_silver\">\n\t\t{{raLabel 'Restricted Accounts'}} will not appear in this list. Ask a team administrator to invite them.\n\t<\/p>\n{{else}}\n\t<p class=\"small_bottom_margin\">\n\t\tType or select the names of team members that you would like to invite:\n\t<\/p>\n{{\/if}}\n\n\t<div id=\"select_invite_channel_members_holder\">\n\t\n\t\t<p class=\"no_bottom_margin\">\n\t\t\t<select multiple=\"multiple\" id=\"select_invite_channel_members\" size=\"30\">\n\t\t\t\t{{#each invite_members}}\n\t\t\t\t\t<option value=\"{{this.member.id}}\" data-additional-search-field=\"@{{this.member.name}}\"{{#if this.preselected}} selected=\"selected\"{{\/if}}>{{#if this.member.real_name}}{{this.member.real_name}} \u2022 {{this.member.name}}{{else}}{{this.member.name}}{{\/if}}{{#if this.member.is_restricted}} ({{raLabel 'Restricted Account'}}){{\/if}}<\/option>\n\t\t\t\t{{\/each}}\n\t\t\t<\/select>\t\t\n\t\t<\/p>\n\t\t\n\t<\/div>\n\n";
TS.raw_templates['group_member_invite_list'] = "\t<div id=\"group_invite_member_chooser\" class=\"hidden\">\n\t\t{{#if show_ra_tip}}\n\t\t\t<p class=\"no_bottom_margin\">\n\t\t\t\tType or select the names of team members that you would like to invite:\n\t\t\t<\/p>\n\t\t\t<p class=\"small_bottom_margin mini subtle_silver\">\n\t\t\t\t{{raLabel 'Restricted accounts'}} may not appear below. Ask a team administrator to invite them.\n\t\t\t<\/p>\n\t\t{{else}}\n\t\t\t<p class=\"small_bottom_margin\">\n\t\t\t\tType or select the names of team members that you would like to invite:\n\t\t\t<\/p>\n\t\t{{\/if}}\n\t\n\t\t<div id=\"select_invite_group_members_holder\">\n\t\t\n\t\t\t<p class=\"no_bottom_margin\">\n\t\t\t\t<select multiple=\"multiple\" id=\"select_invite_group_members\" size=\"30\">\n\t\t\t\t\t{{#each invite_members}}\n\t\t\t\t\t\t<option value=\"{{this.member.id}}\" data-additional-search-field=\"@{{this.member.name}}\"{{#if this.preselected}} selected=\"selected\"{{\/if}}>{{#if this.member.real_name}}{{this.member.real_name}} \u2022 {{this.member.name}}{{else}}{{this.member.name}}{{\/if}}{{#if this.member.is_restricted}} ({{raLabel 'Restricted Account'}}){{\/if}}<\/option>\n\t\t\t\t\t{{\/each}}\n\t\t\t\t<\/select>\t\t\n\t\t\t<\/p>\n\t\t\t\n\t\t<\/div>\n\t\t\n\t\t<p class=\"top_margin\">\n\t\t\t<label class=\"checkbox no_margin\">\n\t\t\t\t<input id=\"archive_access_cb\" checked=\"checked\" type=\"checkbox\" class=\"small_right_margin\" \/> Invited members will have access to the {{groupCopy}} archives\n\t\t\t<\/label>\n\t\t<\/p>\n\t<\/div>\n\t\n\t\n\t<div id=\"group_invite_archives_prompt\" class=\"\">\n\t\t<p class=\"bold\">Should new members be allowed to see the {{groupCopy}}'s message history?<\/p>\n\t\t\n\t\t<p>If <strong>Yes<\/strong>, new members will be invited and will have access to this {{groupCopy}}'s entire message history.<\/p> \n\t\t<p>If <strong>No<\/strong>, this {{groupCopy}} will be renamed and archived. A new {{groupCopy}} will be created with