TS.raw_templates['prefs_win_lin_ssb'] = "<h2 id=\"prefs_app_settings\" class=\"inline_block\">App Settings<\/h2>\n<p>\n\t<label class=\"checkbox\">\n\t\t<input id=\"winssb_launch_on_start_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\tLaunch app on login\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"winssb_run_from_tray_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\tLeave app running in notification area when the window is closed\n\t<\/label>\n\t<label class=\"checkbox\" id=\"winssb_disable_hw_acceleration_pref\">\n\t\t<input id=\"winssb_disable_hw_acceleration_cb\" checked=\"unchecked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\tDisable hardware acceleration<br>\n\t\t<span id=\"winssb_disable_hw_acceleration_note\" class=\"normal\">{{#unless show_lin_ssb_prefs}}If you use a Windows Classic theme, this can resolve rendering issues. {{\/unless}}Restart Slack for this preference to take effect.<\/span>\n\t<\/label>\n<\/p>";
TS.raw_templates['prefs_messages_media'] = "<h2>Message Display<\/h2>\n\n<h4 id=\"prefs_message_theme\" class=\"inline_block\">Message Theme<\/h4>\n<div class=\"bottom_margin clearfix\">\n\t<div class=\"col span_1_of_2\">\n\t\t<label class=\"radio\">\n\t\t\t<input name=\"messages_theme_select\" type=\"radio\" value=\"light_with_avatars\" data-inline-saver=\"#prefs_message_theme\">\n\t\t\tClean: <span class='normal'>Clear, friendly, and focused.<\/span>\n\t\t<\/label>\n\t\t<label class=\"radio\">\n\t\t\t<input name=\"messages_theme_select\" type=\"radio\" value=\"dense\" data-inline-saver=\"#prefs_message_theme\">\n\t\t\tCompact: <span class='normal'>The most messages you can fit on screen at once.<\/span>\n\t\t<\/label>\n\t<\/div>\n\t<div class=\"col span_1_of_2\">\n\t\t<img src=\"{{versioned_prefs_messages_clean}}\" class=\"prefs_messages_theme_preview {{#unless clean_theme}}hidden{{\/unless}}\">\n\t\t<img src=\"{{versioned_prefs_messages_compact}}\" class=\"prefs_messages_theme_preview {{#unless compact_theme}}hidden{{\/unless}}\">\n\t<\/div>\n<\/div>\n\n<h4 id=\"prefs_display_options\" class=\"inline_block\">Display Options<\/h4>\n<p class=\"large_bottom_margin\">\n\t<label class=\"checkbox\">\n\t\t<input id=\"show_typing_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\tDisplay information about who is currently typing a message\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"display_real_names_override_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\tDisplay real names <span id=\"display_real_names_default\">(team default)<\/span> instead of usernames <span id=\"display_usernames_default\">(team default)<\/span>\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"time24_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\tShow times with 24-hour clock <span class=\"normal\">(16:00 rather than 4:00 PM)<\/span>\n\t<\/label>\n\t{{#feature flag=\"feature_lato_2_ssb\"}}\n\t\t{{#isOurApp}}\n\t\t\t{{! We don't show the Lato 2 pref in the SSB, since we load it locally for all users }}\n\t\t{{else}}\n\t\t\t<label class=\"checkbox\">\n\t\t\t\t<input id=\"load_lato_2_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\t\t\tUse font with <a href=\"http:\/\/www.latofonts.com\/2014\/02\/27\/lato-2-0-released\/\" target=\"{{newWindowName}}\">extended latin character set<\/a><\/span><br>\n\t\t\t\t<span class=\"normal\">If you use a language with accented characters, this will improve font appearance. Requires reload to take effect.<\/span>\n\t\t\t<\/label>\n\t\t{{\/isOurApp}}\n\t{{else}}\n\t\t<label class=\"checkbox\">\n\t\t\t<input id=\"load_lato_2_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\t\tUse font with <a href=\"http:\/\/www.latofonts.com\/2014\/02\/27\/lato-2-0-released\/\" target=\"{{newWindowName}}\">extended latin character set<\/a><\/span><br>\n\t\t\t<span class=\"normal\">If you use a language with accented characters, this will improve font appearance. Requires reload to take effect.<\/span>\n\t\t<\/label>\n\t{{\/feature}}\n<\/p>\n\n<h4 id=\"prefs_emoji_style\" class=\"inline_block\">Emoji Style<\/h2>\n<p id=\"prefs_emoji\">\n\t<label class=\"radio\">\n\t\t<input name=\"emoji_mode_select\" type=\"radio\" value=\"default\" data-inline-saver=\"#prefs_emoji_style\">\n\t\t<span class=\"emoji_label\">Apple\/International Style<\/span>\n\t\t{{> prefs_emoji_preview force_style='apple' use_skin_tone_modifier=true}}\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"emoji_mode_select\" type=\"radio\" value=\"google\" data-inline-saver=\"#prefs_emoji_style\">\n\t\t<span class=\"emoji_label\">Google Hangouts Style<\/span>\n\t\t{{> prefs_emoji_preview force_style='google'}}\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"emoji_mode_select\" type=\"radio\" value=\"twitter\" data-inline-saver=\"#prefs_emoji_style\">\n\t\t<span class=\"emoji_label\">Twitter Style<\/span>\n\t\t{{> prefs_emoji_preview force_style='twitter' use_skin_tone_modifier=true}}\n\t<\/label>\n\t<label class=\"radio small_bottom_margin\">\n\t\t<input name=\"emoji_mode_select\" type=\"radio\" value=\"emojione\" data-inline-saver=\"#prefs_emoji_style\"> \n\t\t<span class=\"emoji_label\">Emoji One Style<\/span>\n\t\t{{> prefs_emoji_preview force_style='emojione'}}\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"emoji_mode_select\" type=\"radio\" value=\"as_text\" data-inline-saver=\"#prefs_emoji_style\">\n\t\tNone: <span class=\"normal\">plain text only<\/span>\n\t<\/label>\n<\/p>\n{{#feature flag=\"feature_jumbomoji\"}}\n<p class=\"large_bottom_margin\">\n\t<label class=\"checkbox\">\n\t\t<input id=\"jumbomoji_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_emoji_style\">\n\t\tShow Jumbomoji<br>\n\t\t<span class=\"normal\">Messages and comments containing only a single emoji will be displayed large<\/span>\n\t<\/label>\n<\/p>\n{{\/feature}}\n\n\n<h4 id=\"prefs_convert_emoticons\" class=\"inline_block\">Convert Emoticons<\/h4>\n<p class=\"large_bottom_margin\">\n\t<label class=\"checkbox\">\n\t\t<input id=\"convert_emoticons_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_convert_emoticons\">\n\t\tConvert my typed emoticons to emoji, so &nbsp;:D&nbsp; becomes {{{emojiGraphicReplace \":smile:\"}}}\n\t<\/label>\n<\/p>\n\n<hr>\n\n<h2 id=\"prefs_inline_media\" class=\"large_top_margin inline_block\">Inline Media & Links<\/h2>\n<p>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_internal_inline_imgs_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\tExpand images and files uploaded to Slack\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_inline_imgs_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\tExpand links to images, video and audio from external sources\n\t<\/label>\n\t<label class=\"checkbox large_left_margin\">\n\t\t<input id=\"dont_obey_inline_img_limit_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\tEven if it is an image larger than {{convertFilesize inline_img_byte_limit}}\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_non_media_attachments_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\tExpand website links to show a preview of the content, when available\n\t<\/label>\n<\/p>";
TS.raw_templates['prefs_themes'] = "<div id=\"prefs_themes\">\n\t<h2 id=\"prefs_sidebar_theme\" class=\"inline_block\">Sidebar Theme<\/h2>\n\t<p>Customize the look of the <strong>{{currentTeamName}}<\/strong> team. Only you will see this.<\/p>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_default}}\" class=\"theme_thumb\" alt=\"Aubergine Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"default_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tAubergine\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_hoth}}\" class=\"theme_thumb\" alt=\"Hoth Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"hoth_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tHoth\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_monument}}\" class=\"theme_thumb\" alt=\"Monument Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"monument_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tMonument\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_chocolate}}\" class=\"theme_thumb\" alt=\"Choco Mint Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"chocolate_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tChoco Mint\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_ocean}}\" class=\"theme_thumb\" alt=\"Ochin Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"ocean_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tOchin\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_workhard}}\" class=\"theme_thumb\" alt=\"Work Hard Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"workhard_theme\" data-inline-saver=\"#prefs_sidebar_theme\">\n\t\tWork Hard\n\t<\/label>\n\n\t<hr class=\"clear_both large_top_margin large_bottom_margin\">\n\n\t<h4 id=\"prefs_sidebar_theme_accessible\" class=\"inline_block bottom_margin\">Accessible Themes<\/h4><br>\n\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_solanum}}\" class=\"theme_thumb\" alt=\"Solanum Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"solanum_theme\" data-inline-saver=\"#prefs_sidebar_theme_accessible\">\n\t\tProtanopia & Deuteranopia\n\t<\/label>\n\t<label class=\"radio theme_label\">\n\t\t<img src=\"{{versioned_theme_thumb_brinjal}}\" class=\"theme_thumb\" alt=\"Brinjal Theme\">\n\t\t<input name=\"sidebar_theme_rd\" type=\"radio\" value=\"brinjal_theme\" data-inline-saver=\"#prefs_sidebar_theme_accessible\">\n\t\tTritanopia\n\t<\/label>\n\n\t<hr class=\"clear_both large_top_margin large_bottom_margin\">\n\n\t<p id=\"customize_theme_info\" class=\"{{#if show_customization_ui}}hidden{{\/if}}\">\n\t\tFeeling adventurous?<br>\n\t\t<a id=\"customize_theme_toggle\" class=\"bold\">Customize your theme and share it with others<\/a>\n\t<\/p>\n\n\t<div id=\"prefs_themes_customize\" class=\"{{#unless show_customization_ui}}hidden{{\/unless}}\">\n\t\t<h4 class=\"bottom_margin\">Custom Theme<\/h4>\n\t\t<label>\n\t\t\tColumn BG<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='column_bg' data-hex='{{theme.column_bg}}' style=\"background-color: {{theme.column_bg}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_column_bg_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.column_bg}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tMenu BG Hover<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='menu_bg' data-hex='{{theme.menu_bg}}' style=\"background-color: {{theme.menu_bg}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_menu_bg_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.menu_bg}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tActive Item<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='active_item' data-hex='{{theme.active_item}}' style=\"background-color: {{theme.active_item}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_active_item_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.active_item}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tActive Item Text<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='active_item_text' data-hex='{{theme.active_item_text}}' style=\"background-color: {{theme.active_item_text}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_active_item_text_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.active_item_text}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tHover Item<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='hover_item' data-hex='{{theme.hover_item}}' style=\"background-color: {{theme.hover_item}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_hover_item_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.hover_item}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tText Color<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='text_color' data-hex='{{theme.text_color}}' style=\"background-color: {{theme.text_color}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_text_color_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.text_color}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label>\n\t\t\tActive Presence<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='active_presence' data-hex='{{theme.active_presence}}' style=\"background-color: {{theme.active_presence}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_active_presence_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.active_presence}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t<label class=\"large_bottom_margin\">\n\t\t\tMention Badge<br>\n\t\t\t<span class=\"custom_theme_label\">\n\t\t\t\t<span class=\"color_swatch\" data-theme-element='badge' data-hex='{{theme.badge}}' style=\"background-color: {{theme.badge}};\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"color_badge_hex\" class=\"small color_hex monospace borderless\" value=\"{{theme.badge}}\" maxlength=\"7\">\n\t\t\t<\/span>\n\t\t<\/label>\n\n\t\t<label class=\"clear_both full_width\">\n\t\t\tCopy and paste these values to share your custom theme with others:<br>\n\t\t\t<input type=\"text\" name=\"sidebar_theme_custom\" id=\"sidebar_theme_custom\" class=\"small monospace very_small_top_margin\">\n\t\t<\/label\n\t\t\n\t<\/div>\n<\/div>";
TS.raw_templates['prefs_search'] = "<h2 id=\"prefs_search_exclusion\" class=\"inline_block\">Search Exclusion<\/h2>\n<p>Add channels here that should never show in search results.<\/p>\n<p>\n\t<label for=\"search_channel_exclusion\">Never show search results from these channels:<\/label>\t\t\n\t<select multiple=\"multiple\" id=\"search_channel_exclusion\" size=\"30\" data-inline-saver=\"#prefs_search_exclusion\">\n\t\t<optgroup label=\"Active Channels\">\n\t\t\t{{#each active_channels}}\n\t\t\t\t<option value=\"{{this.channel.id}}\"{{#if this.search_channel_exclusion}} selected{{\/if}}>#{{this.channel.name}}<\/option>\n\t\t\t{{\/each}}\n\t\t<\/optgroup>\n\t\t<optgroup label=\"Archived Channels\">\n\t\t\t{{#each archived_channels}}\n\t\t\t\t<option value=\"{{this.channel.id}}\"{{#if this.search_channel_exclusion}} selected{{\/if}}>#{{this.channel.name}}<\/option>\n\t\t\t{{\/each}}\n\t\t<\/optgroup>\n\t<\/select>\n<\/p>";
TS.raw_templates['prefs_read_state_tracking'] = "<h2 id=\"prefs_read_state_tracking\" class=\"inline_block\">Marking Messages as Read<\/h2>\n<p>By default, Slack marks messages as read as soon as you view the {{channelDmOrGroupCopy}}. You can adjust this behavior if you like:<\/p>\n<p class=\"large_bottom_margin\">\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"immediate_scroll\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\tAutomatically scroll me to the oldest unread message and mark the channel as having been read.\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"immediate\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\tDo not scroll me to the oldest unread message, but still mark the messages as having been read.\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"default\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\tDo not scroll me or automatically mark messages as read \u2014 wait until I've seen the oldest unread message.\n\t<\/label>\n<\/p>\n\n<h4>Tips!<\/h4>\n<p class=\"small_bottom_margin\">{{{emojiGraphicReplace \":raising_hand::skin-tone-6:\"}}} Need to come back to something? You can set your own unread-point by selecting Mark Unread from the message menu (or hold <em>{{#isMac}}Option{{else}}Alt{{\/isMac}}<\/em> and click on a message).<\/p>\n<p class=\"small_bottom_margin\">{{{emojiGraphicReplace \":information_desk_person::skin-tone-2:\"}}} Mark a {{channelDmOrGroupCopy}} as read by hitting the Esc key while viewing it.<\/p>\n<p>{{{emojiGraphicReplace \":ok_woman::skin-tone-4:\"}}} Hold down <em>Shift<\/em> while pressing the <em>Esc<\/em> key to mark everything in Slack as read (use sparingly).<\/p>";
TS.raw_templates['prefs_advanced'] = "<h2 id=\"prefs_input_options\" class=\"inline_block\">Input Options<\/h2>\n<p>\n\t<label class=\"checkbox\">\n\t\t<input id=\"webapp_spellcheck_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_input_options\">\n\t\tEnable spellcheck on your messages\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"require_at_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_input_options\">\n\t\tOnly show autocomplete for people when the '@' character is used\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"enter_is_special_in_tbt_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_input_options\">\n\t\tWhen typing code with ```, <em>Enter<\/em> should not send the message<br \/>\n\t\t<span class=\"normal subtle_silver\">With this checked use <em>Shift+Enter<\/em> to send<\/span>\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"arrow_history_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_input_options\">\n\t\tUse <i class=\"ts_icon ts_icon_square_arrow_up ts_icon_inherit\"><\/i> and <i class=\"ts_icon ts_icon_square_arrow_down ts_icon_inherit\"><\/i> to cycle through your typed history<br>\n\t\t<span class=\"normal subtle_silver\">With this checked, use {{#isMac}}Cmd{{else}}Ctrl{{\/isMac}} + <i class=\"ts_icon ts_icon_square_arrow_up ts_icon_inherit\"><\/i>&nbsp;&nbsp;to edit your last message<\/span>\n\t<\/label>\n<\/p>\n\n{{#if show_app_download_path}}\n\n\t<hr>\n\n\t<div class=\"clearfix\">\n\t\t<h2 id=\"prefs_download_location\" class=\"inline_block small_bottom_margin\">Download Location<\/h2>\n\t\t<p class=\"small_bottom_margin\">Note: this setting is for this computer only and affects all teams you are signed in to.<\/p>\n\t\t<div class=\"col span_3_of_4\"><input id=\"app_download_path_input\" type=\"text\" disabled=\"disabled\" value=\"{{app_download_path}}\" class=\"small\"><\/div>\n\t\t<div class=\"col span_1_of_4\"><button data-action=\"select_ssb_download_path\" class=\"btn btn_outline full_width\">Change &hellip;<\/button><\/div>\n\t<\/div>\n{{\/if}}\n\n<hr>\n\n<h2 id=\"prefs_channel_list\" class=\"inline_block small_bottom_margin\">Channel List<\/h2>\n<p>\n\t<label id=\"sidebar_behavior_select_label\" class=\"select small full_width\">\n\t\t<select id=\"sidebar_behavior_select\" class=\"small\" data-inline-saver=\"#prefs_channel_list\">\n\t\t\t<option value=\"\">Show all {{channelsDmsAndPrivateGroupsCopy}} you have open (default)<\/option>\n\t\t\t{{#tinyspeck}}<option value=\"shrink_left_column\">(TS only) Show all {{channelsDmsAndPrivateGroupsCopy}} you have open, but real tiny<\/option>{{\/tinyspeck}}\n\t\t\t<option value=\"hide_read_channels\">Hide any {{channelsDmsOrGroupsCopy}} which have no unread activity<\/option>\n\t\t\t<option value=\"hide_read_channels_unless_starred\">Hide any channels, etc., which have no unread activity, unless they are starred<\/option>\n\t\t<\/select>\n\t<\/label>\n\t<label for=\"separate_private_channels_cb\" class=\"checkbox no_margin\">\n\t\t<input id=\"separate_private_channels_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_channel_list\">\n\t\tList public and private channels separately <span class=\"normal\">(mobile coming soon)<\/span>\n\t<\/label>\n<\/p>\n\n<hr>\n\n<h2 id=\"prefs_other_options\" class=\"inline_block\">Other Options<\/h2>\n<p>\n\t{{#feature flag=\"feature_chat_sounds\"}}\n\t\t<label class=\"checkbox no_margin\">\n\t\t\t<input id=\"autoplay_chat_sounds_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_other_options\">\n\t\t\tAutomatically play sounds that are used in chat\n\t\t<\/label>\n\t{{\/feature}}\n\n\t<label class=\"checkbox\">\n\t\t<input id=\"pagekeys_handled_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_other_options\">\n\t\t<em>Page up<\/em>, <em>Page down<\/em>, <em>Home<\/em>, and <em>End<\/em> keys always scroll messages\n\t<\/label>\n\n\t{{#isO